import React, { Component } from 'react';
import { StyleSheet, Text, View } from 'react-native';

const WORK_DEFAULT_MINS = 10
const WORK_DEFAULT_SECS = 0
const BREAK_DEFAULT_SECS = 5
const BREKA_DEFAULT_SECS = 0


const Timer = props => (
  <div>
    <span>{props.time.minutes}</span>
    <span> : </span>
    <span>{props.time.seconds}</span>
  </div>
)

export default class App extends React.Component {
  constructor() {
    super()
    this.state = {
      work_minutes: WORK_DEFAULT_MINS,
      work_seconds: WORK_DEFAULT_SECS,
      break_minutes: BREAK_DEFAULT_SECS,
      break_seconds: BREKA_DEFAULT_SECS,
      timer_state: "START" // start or stop
    }
  }

  toggleTimer() {
    this.timer_state = "STOP"
  }

  startTimer() {

  }

  render() {
    return (
      <div>
        <Timer style={styles.container} time={() => this.state}></Timer>
        <button onClick={() => this.toggleTimer()}>{this.state.timer_state}</button>
        <button onClick={() => this.resetTimer()}>RESET</button>
        <span>
          <label>Work Time: </label>
          <label>Mins: </label>
          <input id="work_mins"></input>
          <label>Secs: </label>
          <input id="work_secs"></input>
        </span>
        <span>
          <label>Break Time: </label>
          <label>Mins: </label>
          <input id="break_mins"></input>
          <label>Secs: </label>
          <input id="break_secs"></input>
        </span>
      </div>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});
